import React from 'react';
import { Model } from './model';
import axios from 'axios';
import './../css/nlpinterface.scss';
export class NLPInterface extends React.Component {

  MIN_WORDS = 25;
  MAX_WORDS = 3500;

  state = {
    text: '',
    wordsHint: `You have to write at least ${this.MIN_WORDS} words`,
    loading: false,
    error: false,
    prediction: null,
    original: null,
    pos_tagged: null,
    preprocessed: null,
    emoji: true
  }

  random = () => axios.get("http://a55a39b1.ngrok.io/random")
    .then(res => {
        const data = res.data;
        this.setState({
            text: data.text, 
            wordsHint: this.getWordsHint(data.text), 
            prediction: null,
            original: null,
            pos_tagged: null,
            preprocessed: null,
            emoji: false
    })
})

  updateInput = ({ target: { value } }) => this.setState({ text: value, prediction: null, wordsHint: this.getWordsHint(value) });

  getWordsHint = (text) => {
    let count = text.split(/\s/).length;
    if (count < this.MIN_WORDS) return `You have to write ${this.MIN_WORDS - count} more words`
    if (count >= this.MIN_WORDS) return `You can write up to ${this.MAX_WORDS - count} more words`
  }
  predict = () => {
    if (this.MAX_WORDS - this.state.text.split(/\s/).length < 0) return;

    this.setState({ loading: true, prediction: null });
    axios({
      method: 'POST',
      url: 'http://a55a39b1.ngrok.io/predict',
      data:
      {
        article : this.state.text
      }
    })
    .then (res => {
        console.log(res)
        this.setState({prediction: res.data.prediction, loading: false, original: res.data.original, pos_tagged: res.data.pos_tagged, preprocessed: res.data.preprocessed})
    })
  }

  render() {
    return (
      <div className="container">
        <h1>Fake News Classifier</h1>
        <textarea autoFocus placeholder= 'News to classify?' disabled={this.state.loading} value={this.state.text} onChange={this.updateInput}>
        </textarea>

        <span className="hint"> {this.state.wordsHint}</span>

        <button disabled={this.state.loading} className="random" onClick={this.random}>
          Load random News from test dataset ? <span>Click here</span>
          <span role="img" className="emoji bounce">
            {this.state.emoji ? "üëÜüèº" : ""}
          </span>
        </button>
        <button disabled={
          this.state.loading ||
          this.MIN_WORDS - this.state.text.split(/\s/).length > 0 ||
          this.MAX_WORDS - this.state.text.split(/\s/).length < 0} className="cta" onClick={this.predict}> Predict </button>

        {this.state.loading ? <h1>Classifying ...</h1> : ''}

        {this.state.error ? <h1>ERROR</h1> : ''}

        <h1 className={this.state.prediction}>
          {this.state.prediction}
        </h1>

        <Model
          prediction={this.state.prediction}
          original={this.state.original}
          pos_tagged={this.state.pos_tagged}
          preprocessed={this.state.preprocessed}
        />
      </div>
    )
  }
}
