import React from 'react';
import axios from 'axios';
import './../css/interface.scss';
export class Interface extends React.Component {

  MIN_WORDS = 25;
  MAX_WORDS = 3500;

  state = {
    text: '',
    wordsHint: `You have to write at least ${this.MIN_WORDS} words`,
    loading: false,
    error: false,
    prediction: null,
  }

  random = () => axios.get("http://8e04a3fc.ngrok.io/random")
    .then(res => {
        const data = res.data;
        this.setState({
            text: data.text, 
            wordsHint: this.getWordsHint(data.text), 
            prediction: null,
    })
})

  updateInput = ({ target: { value } }) => this.setState({ text: value, prediction: null, wordsHint: this.getWordsHint(value) });

  getWordsHint = (text) => {
    let count = text.split(/\s/).length;
    if (count < this.MIN_WORDS) return `You have to write ${this.MIN_WORDS - count} more words`
    if (count >= this.MIN_WORDS) return `You can write up to ${this.MAX_WORDS - count} more words`
  }
  predict = () => {
    if (this.MAX_WORDS - this.state.text.split(/\s/).length < 0) return;

    this.setState({ loading: true, prediction: null });
    axios({
      method: 'POST',
      url: 'http://8e04a3fc.ngrok.io/predict',
      data:
      {
        article : this.state.text
      }
    })
    .then (res => {
        console.log(res)
        let pred;
        if (res.data.prediction == 'FAKE'){
          pred = '⛔ FAKE News ⛔';
        } else if (res.data.pred == 'REAL'){
          pred = 'REAL News';
        }
        this.setState({prediction: pred, loading: false})
    })
  }

  render() {
    return (
      <div className="container">
        <h1>Fake News Classifier</h1>
        <textarea placeholder= 'News to classify?' disabled={this.state.loading} value={this.state.text} onChange={this.updateInput}>
        </textarea><br/>

        <span className="hint"> {this.state.wordsHint}</span><br/>

        <button disabled={this.state.loading} className="random" onClick={this.random}>
          <p>Load random News from test dataset ? </p><span>Click here</span>
        </button><br/>
        <div className="button-container">
        <button disabled={
          this.state.loading ||
          this.MIN_WORDS - this.state.text.split(/\s/).length > 0 ||
          this.MAX_WORDS - this.state.text.split(/\s/).length < 0} className="cta" onClick={this.predict}> Predict </button></div><br/>

        {this.state.loading ? <h1 className="loading">Classifying ...</h1> : ''} <br/>

        {this.state.error ? <h1 className="loading">ERROR</h1> : ''} <br/>

        <h1 className={this.state.prediction}>
          {this.state.prediction}
        </h1>
      </div>
    )
  }
}
